import { mapState } from 'vuex';
import validate from '@/utils/helpers/validate';
export default {
  name: 'addModal',
  props: {
    modal: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: '角色新增'
    },
    okText: {
      type: String,
      default: '确定'
    },
    cancelText: {
      type: String,
      default: '取消'
    }
  },
  computed: mapState(['roleStore']),
  data: function data() {
    return {
      addModal: false,
      addLoading: false,
      addForm: {
        name: '',
        code: '',
        remark: ''
      },
      ruleValidate: {
        name: [{
          required: true,
          message: '角色名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '角色名称不能多于10个字符',
          trigger: 'blur'
        }],
        code: [{
          required: true,
          message: '角色编码不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '角色编码不能多于10个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  watch: {
    modal: function modal(val) {
      if (val) {
        // 对话框显示时执行
        this.addModal = val;
      }
    },
    addModal: function addModal(val) {
      if (!val) {
        this.$emit('on-modal-change', val);
      }
    }
  },
  methods: {
    handleOk: function handleOk() {
      var _this = this;
      this.$refs.addForm.validate(function (valid) {
        if (valid) {
          _this.addLoading = true;
          _this.$store.dispatch('addRole', {
            data: _this.addForm
          }).then(function () {
            _this.$Message.success(_this.title + '成功！');
            _this.handleCancel();
            _this.$emit('on-refresh-page');
          }, function (res) {
            _this.addLoading = false;
            _this.$Message.error(res.data.message);
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.resetFields();
      this.addModal = false;
      this.addLoading = false;
    },
    resetFields: function resetFields() {
      this.$refs.addForm.resetFields();
    }
  }
};
