import { mapState } from 'vuex';
import validate from '@/utils/helpers/validate';
export default {
  name: 'modifyModal',
  props: {
    id: {
      type: String,
      default: ''
    },
    modal: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: '角色编辑'
    },
    okText: {
      type: String,
      default: '确定'
    },
    cancelText: {
      type: String,
      default: '取消'
    }
  },
  computed: mapState(['roleStore']),
