          type: 'string',
          max: 10,
          message: '角色名称不能多于10个字符',
          trigger: 'blur'
        }],
        code: [{
          required: true,
          message: '角色编码不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '角色编码不能多于10个字符',
          trigger: 'blur'
        }]
      }
    };
