      var _this = this;
      this.$store.dispatch('showRole', {
        params: {
          id: this.modifyForm.id
        }
      }).then(function () {
        _this.modifyForm = _this.roleStore.item;
      });
    },
    handleOk: function handleOk() {
      var _this2 = this;
      this.$refs.modifyForm.validate(function (valid) {
        if (valid) {
          _this2.modifyLoading = true;
          _this2.$store.dispatch('modifyRole', {
            data: _this2.modifyForm
          }).then(function () {
            _this2.handleCancel();
            _this2.$Message.success(_this2.title + '成功！');
            _this2.$emit('on-refresh-page');
          }, function (res) {
            _this2.modifyLoading = false;
            _this2.$Message.error(res.data.message);
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.resetFields();
      this.modifyModal = false;
      this.modifyLoading = false;
    },
    resetFields: function resetFields() {
      this.$refs.modifyForm.resetFields();
    }
  }
};
