import { mapState } from 'vuex';
import validate from '@/utils/helpers/validate';
export default {
  name: 'addModal',
  props: {
    modal: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: '文件附件新增'
    },
    okText: {
      type: String,
      default: '确定'
    },
    cancelText: {
      type: String,
      default: '取消'
    }
  },
  computed: mapState(['fileAttachStore']),
  data: function data() {
    return {
      addModal: false,
      addLoading: false,
      addForm: {
        tableName: '',
        recordId: '',
        name: '',
        url: '',
        iconUrl: '',
        previewUrl: '',
        createAt: 0,
        createBy: '',
        createName: '',
        updateAt: 0,
        updateBy: '',
        updateName: '',
        isDel: 0,
        isTest: 0
      },
      ruleValidate: {
        value: [{
          required: false,
          message: '数值不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          message: '数值必须为数字',
          trigger: 'blur',
          validator: validate.validateInputNumber
        }],
        tableName: [{
          required: true,
          message: '表名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '表名称不能多于10个字符',
          trigger: 'blur'
        }],
        recordId: [{
          required: true,
          message: '记录ID不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '记录ID不能多于10个字符',
          trigger: 'blur'
        }],
        name: [{
          required: true,
          message: '文件原名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '文件原名称不能多于10个字符',
          trigger: 'blur'
        }],
        url: [{
          required: true,
          message: '文件URL不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '文件URL不能多于10个字符',
          trigger: 'blur'
        }],
        iconUrl: [{
          required: true,
          message: '文件图标URL不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '文件图标URL不能多于10个字符',
          trigger: 'blur'
        }],
        previewUrl: [{
          required: true,
          message: '文件预览URL不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '文件预览URL不能多于10个字符',
          trigger: 'blur'
        }],
        createAt: [{
          required: true,
          message: '创建时间不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '创建时间不能多于10个字符',
          trigger: 'blur'
        }],
        createBy: [{
          required: true,
          message: '创建人ID不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '创建人ID不能多于10个字符',
          trigger: 'blur'
        }],
        createName: [{
          required: true,
          message: '创建人名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '创建人名称不能多于10个字符',
          trigger: 'blur'
        }],
        updateAt: [{
          required: true,
          message: '更新时间不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '更新时间不能多于10个字符',
          trigger: 'blur'
        }],
        updateBy: [{
          required: true,
          message: '更新人ID不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '更新人ID不能多于10个字符',
          trigger: 'blur'
        }],
        updateName: [{
          required: true,
          message: '更新人名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '更新人名称不能多于10个字符',
          trigger: 'blur'
        }],
        isDel: [{
          required: true,
          message: '是否删除不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '是否删除不能多于10个字符',
          trigger: 'blur'
        }],
        isTest: [{
          required: true,
          message: '是否测试不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 10,
          message: '是否测试不能多于10个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  watch: {
    modal: function modal(val) {
      if (val) {
        // 对话框显示时执行
      }
    },
    addModal: function addModal(val) {
      if (!val) {
        this.$emit('on-modal-change', val);
      }
    }
  },
  methods: {
    handleOk: function handleOk() {
      var _this = this;
      this.$refs.addForm.validate(function (valid) {
        if (valid) {
          _this.addLoading = true;
          _this.$store.dispatch('addFileAttach', {
            data: _this.addForm
          }).then(function () {
            _this.$Message.success(_this.title + '成功！');
            _this.handleCancel();
            _this.$emit('on-refresh-page');
          }, function (res) {
            _this.addLoading = false;
            _this.$Message.error(res.data.message);
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.resetFields();
      this.addModal = false;
      this.addLoading = false;
    },
    resetFields: function resetFields() {
      this.$refs.addForm.resetFields();
    }
  }
};
