import { mapState } from 'vuex';
import validate from '@/utils/helpers/validate';
export default {
  name: 'addModal',
  props: {
    modal: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: '用户新增'
    },
    okText: {
      type: String,
      default: '确定'
    },
    cancelText: {
      type: String,
      default: '取消'
    }
  },
  computed: mapState(['dictionaryStore', 'userStore']),
  created: function created() {},
  mounted: function mounted() {},
  data: function data() {
    return {
      addModal: false,
      addLoading: false,
      addForm: {
        name: '',
        loginName: '',
        pwd: '',
        salt: '',
        mobile: '',
        statusCode: '',
        remark: ''
      },
      ruleValidate: {
        name: [{
          required: true,
          message: '用户名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 18,
          message: '用户名称不能多于18个字符',
          trigger: 'blur'
        }],
        loginName: [{
          required: true,
          message: '登录名称不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 18,
          message: '登录名称不能多于18个字符',
          trigger: 'blur'
        }],
        pwd: [{
          required: true,
          message: '登录密码不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 18,
          message: '登录密码不能多于18个字符',
          trigger: 'blur'
        }],
        mobile: [{
          required: true,
          message: '手机号码不能为空',
          trigger: 'blur'
        }, {
          type: 'string',
          max: 11,
          message: '手机号码不能多于11个字符',
          trigger: 'blur'
        }],
        statusCode: [{
          required: true,
          message: '请选择用户状态',
          trigger: 'change'
        }]
      }
    };
  },
  watch: {
    modal: function modal(val) {
      if (val) {
        // 对话框显示时执行
        this.addModal = val;
        this.initUserStatus();
      }
    },
    addModal: function addModal(val) {
      if (!val) {
        this.$emit('on-modal-change', val);
      }
    }
  },
  methods: {
    initUserStatus: function initUserStatus() {
      this.$store.dispatch('listDictionary', {
        params: {
          type: 'YHZT'
        }
      });
    },
    handleOk: function handleOk() {
      var _this = this;
      this.$refs.addForm.validate(function (valid) {
        if (valid) {
          _this.addLoading = true;
          _this.$store.dispatch('addUser', {
            data: _this.addForm
          }).then(function () {
            _this.$Message.success(_this.title + '成功！');
            _this.handleCancel();
            _this.$emit('on-refresh-page');
          }, function (res) {
            _this.addLoading = false;
            _this.$Message.error(res.data.message);
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.resetFields();
      this.addModal = false;
      this.addLoading = false;
    },
    resetFields: function resetFields() {
      this.$refs.addForm.resetFields();
    }
  }
};
