import { mapState } from 'vuex'

export default {
  name: 'showModal',
  props: {
    id: {type: String, default: ''},
    modal: {type: Boolean, default: false},
    title: {type: String, default: '文件附件查看'},
    okText: {type: String, default: '确定'}
  },
  computed: mapState([
    'fileAttachStore'
  ]),
  data () {
    return {
      showModal: false,
      showLoading: false,
      showForm: {
        id: '',
        tableName: '',
        recordId: '',
        name: '',
        url: '',
        iconUrl: '',
        previewUrl: '',
        createAt: 0,
        createBy: '',
        createName: '',
        updateAt: 0,
        updateBy: '',
        updateName: '',
        isDel: 0,
        isTest: 0
      }
    }
  },
  watch: {
    modal: function (val) {
      if (val && this.id !== '') {
        this.showForm.id = this.id
        this.showModal = val
        this.showFileAttach()
      }
    },
    showModal: function (val) {
      if (!val) {
        this.$emit('on-modal-change', '', val)
      }
    }
  },
  methods: {
    showFileAttach () {
      this.$store.dispatch('showFileAttach', {
        params: {
          id: this.showForm.id
        }
      }).then(() => {
        this.showForm = this.fileAttachStore.item
      })
    },
    handleOk () {
      this.handleCancel()
    },
    handleCancel () {
      this.resetFields()
      this.showModal = false
      this.showLoading = false
    },
    resetFields () {
      this.$refs.showForm.resetFields()
    }
  }
}
