  import SideMenu from './components/side-menu'
  import HeaderBar from './components/header-bar'
  import TagsNav from './components/tags-nav'
  import User from './components/user'
  import ABackTop from './components/a-back-top'
  import Fullscreen from './components/fullscreen'
  import Language from './components/language'
  import ErrorStore from './components/error-store'
  import config from '@/config'
  import { mapMutations, mapActions, mapGetters, mapState } from 'vuex'
  import { getNewTagList, routeEqual, setMenuItemsInSessionstorage, getMenuItemsFromSessionstorage} from '@/libs/util'
  import { iviewAdminRouter } from '@/router/iviewAdmin'
  import minLogo from '@/assets/images/logo-min.jpg'
  import maxLogo from '@/assets/images/logo.jpg'
  import './main.less'

  export default {
    name: 'Main',
    components: {
      SideMenu,
      HeaderBar,
      Language,
      TagsNav,
      Fullscreen,
      ErrorStore,
      User,
      ABackTop
    },
    data () {
      return {
        menuItems: [],
        collapsed: false,
        minLogo,
        maxLogo,
        isFullscreen: false
      }
    },
    computed: {
      ...mapState([
        'iviewAdminAppStore',
        'iviewAdminUserStore'
      ]),
      ...mapGetters([
        'menuList',
        'errorCount'
      ]),
      tagNavList () {
        return this.iviewAdminAppStore.tagNavList
      },
//      tagRouter () {
//        return this.iviewAdminAppStore.tagRouter
//      },
      userAvatar () {
        return this.iviewAdminUserStore.avatarImgPath
      },
      cacheList () {
        const list = ['ParentView', ...this.tagNavList.length ? this.tagNavList.filter(item => !(item.meta && item.meta.notCache)).map(item => item.name) : []]
        return list
      },
      local () {
        return this.iviewAdminAppStore.local
      },
      hasReadErrorPage () {
        return this.iviewAdminAppStore.hasReadErrorPage
      },
      unreadCount () {
        return this.iviewAdminUserStore.unreadCount
      }
    },
    methods: {
      ...mapMutations([
        'setBreadCrumb',
        'setTagNavList',
        'addTag',
        'setLocal',
        'setHomeRoute',
        'closeTag'
      ]),
      ...mapActions([
        'listUserMenu',
        'handleLogin',
        'getUnreadMessageCount'
      ]),
      turnToPage (route) {
        let { name, params, query } = {}
        if (typeof route === 'string') name = route
        else {
          name = route.name
          params = route.params
          query = route.query
        }
        if (name.indexOf('isTurnByHref_') > -1) {
          window.open(name.split('_')[1])
          return
        }
        this.$router.push({
          name,
          params,
          query
        })
      },
      handleCollapsedChange (state) {
        this.collapsed = state
      },
      handleCloseTag (res, type, route) {
        if (type !== 'others') {
          if (type === 'all') {
            this.turnToPage(this.$config.homeName)
          } else {
            if (routeEqual(this.$route, route)) {
              this.closeTag(route)
            }
          }
        }
        this.setTagNavList(res)
      },
      handleClick (item) {
        this.turnToPage(item)
      }
    },
    watch: {
      '$route' (newRoute) {
        const { name, query, params, meta } = newRoute
        this.addTag({
          route: { name, query, params, meta },
          type: 'push'
        })
        this.setBreadCrumb(newRoute)
        this.setTagNavList(getNewTagList(this.tagNavList, newRoute))
        this.$refs.sideMenu.updateOpenName(newRoute.name)
      }
    },
    mounted () {
      /**
       * @description 初始化设置面包屑导航和标签导航
       */
      this.setTagNavList()
      this.setHomeRoute(iviewAdminRouter)
      const { name, params, query, meta } = this.$route
      this.addTag({
        route: { name, params, query, meta }
      })
      this.setBreadCrumb(this.$route)
      // 设置初始语言
      this.setLocal(this.$i18n.locale)
      // 如果当前打开页面不在标签栏中，跳到homeName页
      if (!this.tagNavList.find(item => item.name === this.$route.name)) {
        this.$router.push({
          name: this.$config.homeName
        })
      }
      // 获取未读消息条数
      this.getUnreadMessageCount()

      // 获取当前用户菜单权限
      if (config.checkUserMenu) {
        this.menuItems = getMenuItemsFromSessionstorage()
        if (Object.keys(this.menuItems).length <= 0) {
          this.listUserMenu().then(() => {
            this.menuItems = this.menuList
            setMenuItemsInSessionstorage(this.menuItems)
          })
        }
      } else {
        this.menuItems = this.menuList
      }
    }
  }
