import { mapState } from 'vuex'
import validate from '@/utils/helpers/validate'

export default {
  name: 'addModal',
  props: {
    modal: { type: Boolean, default: false },
    title: { type: String, default: '用户新增' },
    okText: { type: String, default: '确定' },
    cancelText: { type: String, default: '取消' }
  },
  computed: mapState([
    'dictionaryStore',
    'userStore'
  ]),
  created () {},
  mounted () {},
  data () {
    return {
      addModal: false,
      addLoading: false,
      addForm: {
        name: '',
        loginName: '',
        pwd: '',
        salt: '',
        mobile: '',
        statusCode: '',
        remark: ''
      },
      ruleValidate: {
        name: [
          { required: true, message: '用户名称不能为空', trigger: 'blur' },
          { type: 'string', max: 18, message: '用户名称不能多于18个字符', trigger: 'blur' }
        ],
        loginName: [
          { required: true, message: '登录名称不能为空', trigger: 'blur' },
          { type: 'string', max: 18, message: '登录名称不能多于18个字符', trigger: 'blur' }
        ],
        pwd: [
          { required: true, message: '登录密码不能为空', trigger: 'blur' },
          { type: 'string', max: 18, message: '登录密码不能多于18个字符', trigger: 'blur' }
        ],
        mobile: [
          { required: true, message: '手机号码不能为空', trigger: 'blur' },
          { type: 'string', max: 11, message: '手机号码不能多于11个字符', trigger: 'blur' }
        ],
        statusCode: [
          { required: true, message: '请选择用户状态', trigger: 'change' }
        ]
      }
    }
  },
  watch: {
    modal: function (val) {
      if (val) {
        // 对话框显示时执行
        this.addModal = val
        this.initUserStatus()
      }
    },
    addModal: function (val) {
      if (!val) {
        this.$emit('on-modal-change', val)
      }
    }
  },
  methods: {
    initUserStatus () {
      this.$store.dispatch('listDictionary', {
        params: {
          type: 'YHZT'
        }
      })
    },
    handleOk () {
      this.$refs.addForm.validate((valid) => {
        if (valid) {
          this.addLoading = true
          this.$store.dispatch('addUser', {
            data: this.addForm
          }).then(() => {
            this.$Message.success(this.title + '成功！')
            this.handleCancel()
            this.$emit('on-refresh-page')
          }, (res) => {
            this.addLoading = false
            this.$Message.error(res.data.message)
          })
        }
      })
    },
    handleCancel () {
      this.resetFields()
      this.addModal = false
      this.addLoading = false
    },
    resetFields () {
      this.$refs.addForm.resetFields()
    }
  }
}
